-- database: :memory:
CREATE TABLE IF NOT EXISTS `UserTypes` (`userType_ID` INTEGER PRIMARY KEY, `role` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS `Users` (`user_ID` INTEGER PRIMARY KEY, `username` VARCHAR(255) NOT NULL, `password` VARCHAR(255) NOT NULL, `joinDate` DATETIME, `userType_ID` INTEGER NOT NULL REFERENCES `UserTypes` (`userType_ID`), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS `Customers` (`customer_ID` INTEGER PRIMARY KEY, `name` VARCHAR(255) NOT NULL, `email` VARCHAR(255) NOT NULL, `phone` VARCHAR(255) NOT NULL, `address` VARCHAR(255) NOT NULL, `user_ID` INTEGER NOT NULL REFERENCES `Users` (`user_ID`), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS `Suppliers` (`supplier_ID` INTEGER PRIMARY KEY, `name` VARCHAR(255) NOT NULL, `email` VARCHAR(255) NOT NULL, `phone` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS `Category` (`category_ID` INTEGER PRIMARY KEY, `name` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS `Brands` (`brand_ID` INTEGER PRIMARY KEY, `name` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS `Products` (`product_ID` INTEGER PRIMARY KEY, `name` VARCHAR(255) NOT NULL, `brand_ID` VARCHAR(255) NOT NULL REFERENCES `Brands` (`brand_ID`), `category_ID` INTEGER NOT NULL REFERENCES `Category` (`category_ID`), `unitPrice` FLOAT NOT NULL, `stockQty` INTEGER NOT NULL, `supplier_ID` INTEGER NOT NULL REFERENCES `Suppliers` (`supplier_ID`), `img_url` VARCHAR(255) NOT NULL, `specDetail` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS `Status` (`status_ID` INTEGER PRIMARY KEY, `statusName` VARCHAR(255) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS `Orders` (`order_ID` INTEGER PRIMARY KEY, `customer_ID` INTEGER NOT NULL REFERENCES `Customers` (`customer_ID`), `orderDate` DATETIME, `totalAmount` FLOAT NOT NULL, `status_ID` INTEGER NOT NULL REFERENCES `Status` (`status_ID`), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);

CREATE TABLE IF NOT EXISTS "OrderDetails" (`orderDetail_ID` INTEGER PRIMARY KEY, `order_ID` INTEGER NOT NULL REFERENCES `Orders` (`order_ID`), `product_ID` INTEGER NOT NULL REFERENCES `Products` (`product_ID`), `quantity` INTEGER NOT NULL, `unitPrice` FLOAT NOT NULL, `subtotal` FLOAT NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
